import { FC } from "react";
import {useState, useEffect } from 'react';
import { BooksList, getNewYorkBooks, getListDetails} from "../../../services/api"
import Card from '../../card/index';
import { ThemeProvider } from "styled-components";
import theme from '../../styles/theme'
import { CustomButton } from "../../button/styles"
import { Link } from "react-router-dom";

const Home: FC = () => {

// function App() {
    const [data, setData] = useState<BooksList[]>([])
    const [dataDetails, setDataDetails] = useState<any[]>([])
    const [showList, setShowList] = useState<boolean>(true)
    useEffect(() => {
      getNewYorkBooks().then(data => {
        if (data) {
          setData(data)
        }
      })

     }, [])

    const callListDetails = async (listName: string) => {
        const data = await getListDetails(listName)
        console.log(data)
        setDataDetails(data)
        setShowList(false)
    }

    return (
      <ThemeProvider theme = {theme}>
      <div className="App">
        <header className="App-header">
          <div className='cards'>
            {showList && data?.map(bookList => (
              <Card 
                title={bookList.display_name} 
                updatedDes={bookList.updated} 
                oldestDes={bookList.oldest_published_date} 
                newestDes={bookList.newest_published_date} 
                listName={bookList.list_name_encoded}
                image={bookList.image_url}
                onClick={() => callListDetails(bookList.list_name_encoded)}
              />
            ))}
            {!showList && dataDetails?.map(listDetails => (
              <>
              <div className="container-details">
             <div>
             <img src={listDetails.book_image} /> 
                <p>{listDetails.title}</p>
                <p>{listDetails.description}</p>
             </div>
                <CustomButton>
                <div className='amazonLink'><a target="blank" rel="noreferrer" className="amazonText"href={listDetails.amazon_product_url}>Amazon</a></div>

                </CustomButton>
              <br />
              <Link className="App-link" to="/details">
              <button onClick={() => setShowList(true)}>Back</button>
              </Link>
              </div>
              </>

            ))}
          </div>
        </header>
      </div>
      </ThemeProvider>
    );
    }
export default Home
