const localStorageKeyBooks = 'books'
// const localStorageKeyDetails = 'bookDetails'
// const localStorageListName = 'currentListName'

export const getData =  () => {
const data = window.localStorage.getItem(localStorageKeyBooks);

return data ? JSON.parse(data) : []
}

export const setData =  (results: Array<Record<string, unknown>>) => {
    window.localStorage.setItem(localStorageKeyBooks, JSON.stringify(results))
}
