import { useState, useEffect } from 'react';
import './App.css';
import { BooksList, getNewYorkBooks, getListDetails } from "../src/services/api"
import Card from './components/card/index';

function App() {
  const [data, setData] = useState<BooksList[]>([])
  const [dataDetails, setDataDetails] = useState<any[]>([])
  const [showList, setShowList] = useState<boolean>(true)
  useEffect(() => {
    getNewYorkBooks().then(data => {
      if (data) {
        setData(data)
      }
    })
  }, [])

  const callListDetails = async (listName: string) => {
      const data = await getListDetails(listName)
      setDataDetails(data)
      setShowList(false)
  }

  return (
    <div className="App">
      <header className="App-header">
        <div className='cards'>
          {showList && data?.map(bookList => (
            <Card 
              title={bookList.display_name} 
              updatedDes={bookList.updated} 
              oldestDes={bookList.oldest_published_date} 
              newestDes={bookList.newest_published_date} 
              listName={bookList.list_name_encoded}
              onClick={callListDetails}
            />
          ))}
          {!showList && dataDetails?.map(listDetails => (
            <>
            <div className="container-details">


            <img src={listDetails.book_image}> </img>
            <button onClick={() => setShowList(true)}>Back</button>
            <div>{JSON.stringify(listDetails)}</div>
            </div>
            </>

          ))}
        </div>
      </header>
    </div>
  );
}

export default App;
